SET SQL_MODE="NO_AUTO_VALUE_ON_ZERO";;CREATE TABLE IF NOT EXISTS `content` (`page` text NOT NULL,`nav` smallint(6) NOT NULL default '0',`navtitle` varchar(32) NOT NULL,`title` text,`desc` mediumtext,`keywords` mediumtext,`header` text,`content` longtext NOT NULL,`dependent` tinyint(1) NOT NULL default '0' COMMENT '0 for dependent,1 for independent and 2 for independent ONLY IF the param is set',`dependentparam` varchar(32) default NULL COMMENT 'Has to be an $_GET value',`editable` tinyint(1) NOT NULL default '1',`lastmod` timestamp NOT NULL default CURRENT_TIMESTAMP on update CURRENT_TIMESTAMP,`sitemap` tinyint(1) NOT NULL default '1' COMMENT 'to be displayed in sitemap') ENGINE=MyISAM DEFAULT CHARSET=latin1;;INSERT INTO `content` (`page`,`nav`,`navtitle`,`title`,`desc`,`keywords`,`header`,`content`,`dependent`,`dependentparam`,`editable`,`lastmod`,`sitemap`) VALUES ('/', 1,'Home','Home Page','This is the home page','home, page','Welcome Users!','Welcome to the Home page',0,NULL,1,'2009-01-25 10:56:54',1),('/error',0,'','Page not found','This is the 40x error page','Page, Not Found, 404, Error, no result, search','Page Not Found:','This page was not found. Please go back.',0,NULL,0,'2009-01-25 10:36:50',1),('/about',1,'About Us',NULL,NULL,NULL,'About Us','about us',0,NULL, 1,'2009-01-25 10:56:54',1),('/contact',1,'Contact Us',NULL,NULL,NULL,'Contact Us','contact us somehow',0,NULL,1,'2009-01-25 10:56:54',1);;CREATE TABLE IF NOT EXISTS `dns` (`subdomain` varchar(16) NOT NULL,`link` varchar(32) NOT NULL default '/' COMMENT 'URI only') ENGINE=MyISAM DEFAULT CHARSET=latin1;;INSERT INTO `dns` (`subdomain`,`link`) VALUES ('admin','/admin'),('administrator','/admin'),('home','/');;CREATE TABLE IF NOT EXISTS `mods` (`name` varchar(64) NOT NULL,`enable` tinyint(1) NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;;INSERT INTO `mods` (`name`,`enable`) VALUES('gzip',0);;CREATE TABLE IF NOT EXISTS `mod_vars` (`name` varchar(64) NOT NULL,`value` varchar(64) NOT NULL) ENGINE=MyISAM DEFAULT CHARSET=latin1;;CREATE TABLE IF NOT EXISTS `preferences` (`name` varchar(32) NOT NULL default 'Site Name Here',`owner` varchar(32) NOT NULL default 'The Owner of the Site Here',`copyright` varchar(64) NOT NULL default 'Your Copyright Message Here',`adminemail` varchar(32) NOT NULL default 'admin@test.com') ENGINE=MyISAM DEFAULT CHARSET=latin1;;
